.map-building-detail {
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
  border-radius: 15px 15px 0 0;
  position: fixed;
}


.detail-popup {
  display: none;
  position: absolute;
  left: 80px;
  top: 80px;
  bottom: 0;
  width: 29vw;
  min-width: 400px;
  background-color: white;
  z-index: 2;
  transition: transform 0.4s ease, width 0.4s ease;
  box-shadow: 0px 3.3655px 3.3655px rgba(0, 0, 0, 0.1);
  height: fit-content;  
  border-radius: 15px 15px 0 0;
}

.open {
  display: block;
}


.detail-popup__container {
  padding: 30px;
}


.detail-popup__img {
  border-radius: 10px;
  min-height: 300px;
  height: 20vw;
  width: 240px;
  object-fit: cover;
}

.tag__group-name{
  font-size: 20px;
  font-weight: 700;
  color: #212E44;
  margin-right: 9px;
  margin-left: 8px;
}

.detail-popup__tags {
  display: flex;
  flex-wrap: wrap;
  margin-left: 5px;
  margin-bottom: 35px;

}

.tag__category, .tag-group-architect {
  display: flex;
  align-items: center;
}

.tag__category {
  margin-bottom: 15px;
}


.tag  {
  font-size: 16px;
  font-weight: 700;
  padding: 10px 35px;
  text-align: center;
  border-radius: 30px;
  margin: 0 10px 15px 0
}


.line {
  width: 100%;
  height: 4px;
  border-radius: 30px;
  margin-left: 9px;
}

.tag--style, .line--style {
  background-color: #F0F5FF;
  color: #618EEC;
  stroke: #618EEC;
}

.tag--type, .line--type {
  background-color: #FDF5F2;
  color: #F58975;
  stroke: #F58975;
}

.tag--architect, .line--architect {
  background-color: #FCF1F7;
  color: #AF447F;
  stroke: #AF447F;
}

.tag--small {
  padding: 8px 20px;
  font-size: 12px;
}

.tag--no-margin {
  margin: 0 3px;
  cursor: pointer;
}


.tag__icon {
  margin-left: 4px;
}

.button {
  font-weight: 500;
  font-size: 16px;
  text-decoration: none;
  padding: 9px;
  border-radius: 4px;
  display: block;
  text-align: center;
}

.button--dark {
  background-color: #212E44;
  color: white;
}

.detail-popup__row {
  display: flex;
  margin-bottom: 45px;
  margin-top: -70px;
}

.detail-popup__address {
  position: relative;
  align-self: flex-end;
  margin-left: 20px;
}

.detail-popup__address__street, .detail-popup__address__municipality, .detail-popup__name {
  display: inline-block;
  background-color: white;
  padding: 10px 25px 10px 15px;
  border-radius: 5px;
  color: #212E44;
  width: max-content;
  font-size: 30px;
  font-weight: 700;
}


.detail-popup__address__municipality {
  border-radius: 0 0 5px 5px;
}

.detail-popup__name {
  font-size: 32px;
  position: absolute;
  bottom: 130px;
  max-width: 45vw;
}

.watch__icon {
  display: none;
}

.detail-popup__img-container {
  position: relative;
}



.img-modal {
  display: none;
  position: fixed;
  z-index: 2;
  padding-top: 100px;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  overflow: auto;
  background-color: rgba(33,46,68, .96);
}

.img-modal__content {
  display: flex;
  justify-content: center;
  align-items: center;
}

.img-model__img {
  border-radius: 10px;
  height: 100vw;
}

.btn-close {
  color: white;
  position: absolute;
  top: 10px;
  right: 25px;
  font-size: 35px;
  font-weight: bold;
}

.btn-close:hover,
.btn-close:focus {
  text-decoration: none;
  cursor: pointer;
}




@media only screen and (max-width: 880px) {
  .map-building-detail {
    height: 350px;
    position: relative;
  }
  
  .detail-popup {
    left: 0;
    top: 330px;
    width: 100vw;
    border-radius: 15px;
  }

  .detail-popup__address {
    display: flex;
    flex-direction: column;
  }

  .detail-popup__name {
    max-width: 55vw;
  }
}


@media only screen and (max-width: 600px) {
  .detail-popup__name, .detail-popup__address  {
    position: inherit;
  }
  .detail-popup__row {
    display: block;
  }

  .detail-popup__address {
    margin-left: 0;
  }

  .detail-popup__address__street, .detail-popup__address__municipality, .detail-popup__name {
    padding: 10px 25px 0 0;
    font-size: 25px;
  }

  .detail-popup__img {
    width: 100%;
    min-height: 200px;
    object-fit: cover;
    margin-bottom: 20px;
  }

  .detail-popup__row {
    margin-top: 0;
  }

  .detail-popup__name {
    max-width: 100%;
  }

  .watch__icon {
    display: block;
    position: absolute;
    bottom: 35px;
    right: 10px;
    cursor: pointer;
  } 
}


.btn--back {
  display: none;
  top: 30px;
  left: 30px;
  z-index: 3;
  cursor: pointer;
  position: absolute;
}

.is-visible {
  display: block;
}

